mapper.select:
SELECT MEMBER_CODE_SEQ 
FROM MEMBER_REG 
WHERE ID=#{id}

mapper.update:
UPDATE MEMBER_REG
SET NICK=#{nick}, ADDRNUMBER=#{addrnumber}, ADDR1=#{addr1}
, ADDR2=#{addr2}, ADDR3=#{addr3}, EMAIL=#{email},PHONENUMBER=#{phonenumber} 
WHERE ID=#{id}

UPDATE MEMBER_REG
SET PW=CRYPTPACK.ENCRYPT(#{pw}, #{pw})
WHERE ID=#{id}

mapper.select:
SELECT NICK
FROM MEMBER_REG 
WHERE ID=#{id}
